template.form:
[
    {
        "key": "globals.loadDataFramePipelineMappings",
        "wrappers": [
            "panel"
        ],
        "templateOptions": {
            "label": "Download Options"
        },
        "fieldGroup": [
            {
                "key": "sftp_host",
                "type": "input",
                "templateOptions": {
                    "label": "SFTP Host",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "sftp_username",
                "type": "input",
                "templateOptions": {
                    "label": "SFTP Username",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "sftp_password",
                "type": "input",
                "templateOptions": {
                    "label": "SFTP Password",
                    "type": "password",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "sftpPort",
                "type": "input",
                "defaultValue": 22,
                "templateOptions": {
                    "label": "SFTP Port",
                    "type": "number",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "sftp_input_path",
                "type": "input",
                "templateOptions": {
                    "label": "SFTP Path",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "landing_path",
                "type": "input",
                "templateOptions": {
                    "label": "Landing Path",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "input_buffer_size",
                "type": "input",
                "defaultValue": 65536,
                "templateOptions": {
                    "label": "Input Buffer Size",
                    "type": "number",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "output_buffer_size",
                "type": "input",
                "defaultValue": 65536,
                "templateOptions": {
                    "label": "Output Buffer Size",
                    "type": "number",
                    "placeholder": "",
                    "focus": false
                }
            },
            {
                "key": "read_buffer_size",
                "type": "input",
                "defaultValue": 32768,
                "templateOptions": {
                    "label": "Read Buffer Size",
                    "type": "number",
                    "placeholder": "",
                    "focus": false
                }
            }
        ]
    },
    {
        "key": "globals.readOptions.object",
        "wrappers": [
            "panel"
        ],
        "templateOptions": {
            "label": "Input Options"
        },
        "fieldGroup": [
            {
                "type": "select",
                "key": "format",
                "templateOptions": {
                    "label": "Format",
                    "options": [
                        {
                            "value": "csv",
                            "name": "CSV"
                        },
                        {
                            "value": "json",
                            "name": "JSON"
                        },
                        {
                            "value": "parquet",
                            "name": "Parquet"
                        },
                        {
                            "value": "orc",
                            "name": "Orc"
                        },
                        {
                            "value": "text",
                            "name": "Text"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name",
                    "placeholder": "",
                    "focus": false,
                    "disabled": false,
                    "_flatOptions": true
                }
            },
            {
                "key": "options.encoding",
                "type": "input",
                "templateOptions": {
                    "label": "Encoding",
                    "placeholder": "",
                    "focus": false
                },
                "expressionProperties": {
                    "templateOptions.disabled": "!model.format"
                }
            },
            {
                "key": "options.multiLine",
                "type": "select",
                "defaultValue": "false",
                "templateOptions": {
                    "label": "Multiline?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                },
                "hideExpression": "!model.format || (model.format !== 'csv' && model.format !== 'json') ? true : false"
            },
            {
                "key": "options.header",
                "type": "select",
                "defaultValue": "false",
                "templateOptions": {
                    "label": "Skip Header?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                },
                "hideExpression": "!model.format || model.format !== 'csv' ? true : false"
            },
            {
                "key": "options.sep",
                "type": "input",
                "templateOptions": {
                    "label": "Field Separator",
                    "placeholder": "",
                    "focus": false
                },
                "hideExpression": "!model.format || model.format !== 'csv' ? true : false"
            },
            {
                "key": "options.lineSep",
                "type": "input",
                "templateOptions": {
                    "label": "Line Separator",
                    "placeholder": "",
                    "focus": false
                },
                "hideExpression": "!model.format || (model.format !== 'text' && model.format !== 'json') ? true : false"
            },
            {
                "key": "options.quote",
                "type": "input",
                "templateOptions": {
                    "label": "Field Quote",
                    "placeholder": "",
                    "focus": false
                },
                "hideExpression": "!model.format || model.format !== 'csv' ? true : false"
            },
            {
                "key": "options.escape",
                "type": "input",
                "templateOptions": {
                    "label": "Field Escape Character",
                    "placeholder": "",
                    "focus": false
                },
                "hideExpression": "!model.format || model.format !== 'csv' ? true : false"
            },
            {
                "key": "options.primitivesAsString",
                "type": "select",
                "defaultValue": "false",
                "templateOptions": {
                    "label": "Primitive As String?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                },
                "hideExpression": "!model.format || model.format !== 'json' ? true : false"
            },
            {
                "key": "options.inferSchema",
                "type": "select",
                "defaultValue": "false",
                "templateOptions": {
                    "label": "Infer Schema?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                },
                "hideExpression": "!model.format || model.format !== 'csv' ? true : false"
            },
            {
                "key": "options.samplingRatio",
                "type": "input",
                "templateOptions": {
                    "label": "Sampling Ration",
                    "type": "number"
                },
                "hideExpression": "model.format === 'csv' || model.format === 'json' ? false : true",
                "expressionProperties": {
                    "templateOptions.disabled": "model.options.inferSchema === 'true' || model.format === 'json' ? false : true"
                }
            }
        ]
    },
    {
        "key": "globals",
        "wrappers": [
            "panel"
        ],
        "templateOptions": {
            "label": "Processing Options"
        },
        "fieldGroup": [
            {
                "key": "executeColumnCleanup",
                "type": "select",
                "defaultValue": "true",
                "templateOptions": {
                    "label": "Standardize Column Names?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                }
            },
            {
                "key": "addRecordId",
                "type": "select",
                "defaultValue": "true",
                "templateOptions": {
                    "label": "Add Record Id?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                }
            },
            {
                "key": "addFileId",
                "type": "select",
                "defaultValue": "true",
                "templateOptions": {
                    "label": "Add File Id?",
                    "options": [
                        {
                            "value": "true",
                            "name": "True"
                        },
                        {
                            "value": "false",
                            "name": "False"
                        }
                    ],
                    "valueProp": "value",
                    "labelProp": "name"
                }
            }
        ]
    }
]
